INSERT INTO `page` VALUES (1,'Sejarah','<p xss=removed><span xss=\"removed\">Bahwa dengan rahmat dan karunia Tuhan Yang Maha Esa, tercapailah kemerdekaan bangsa dan negara Republik Indonesia.</span><span xss=\"removed\"><o></o></span></p>\r\n<p xss=removed><span xss=\"removed\">Bahwa kemerdekaan itu patut disyukuri dan dikembang-lestarikan melalui karya nyata yaitu pembangunan guna mewujudkan masyarakat yang adil dan makmur berdasarkan Pancasila.</span><span xss=\"removed\"><o></o></span></p>\r\n<p xss=removed><span xss=\"removed\">Bahwa untuk membangun dan membentuk manusia Indonesia seutuhnya bukan hanya menjadi beban dan tugas pemerintah saja, melainkan juga menuntut peran serta masyarakat dalam karya pendidikan yang mengarah pada pembinaan dan membentuk manusia yang tangguh dan tanggap dalam menghadapi tuntutanperkembangan secara menyeluruh.</span><span xss=\"removed\"><o></o></span></p>\r\n<p xss=removed><span xss=\"removed\">Bahwa sadar akan cita-cita ini Yayasan Widya Utama terpanggil untuk ikut berperan serta dalam mencerdaskankehidupan bangsa dengan menyelenggarakan lembaga pendidikan tinggi yang diberi nama Sekolah Tinggi Manajemen Informatika dan Komputer.</span><span xss=\"removed\"><o></o></span></p>\r\n<p xss=removed><span xss=\"removed\">Bahwa untuk mewujudkan cita-cita tersebut diatas, maka lahirlah</span><span xss=\"removed\">Sekolah Tinggi Manajemen Informatika dan Komputer</span><span xss=\"removed\">Widya Utama pada tanggal 14 Agustus 1999 dan ditetapkan berdasarkan</span><span xss=\"removed\"><span xss=\"removed\">Surat Keputusan Mendiknas No.</span></span><span xss=\"removed\"><span xss=\"removed\">169/D/O/2001 tanggal 3 September 2001</span></span><span xss=\"removed\">sebagai sarana untuk mencapai tujuan dalam keikutsertaan untuk mencerdaskan kehidupan bangsa.</span><span xss=\"removed\"><o></o></span></p>',NULL,NULL,'sejarah','','',0),(3,'Kurikulum','<table width=\"477\" height=\"913\" xss=\"removed\" border=\"1\"><colgroup><col width=\"42\"